//       nodes={nodes}
//       edges={edges}
//       onNodesChange={onNodesChange}
//       fitView
//     >
//       <Panel position="top-right">
//         <button onClick={() => onLayout('TB')}>vertical layout</button>
//         <button onClick={() => onLayout('LR')}>horizontal layout</button>
//       </Panel>
//     </ReactFlow>
//   );
// };

export default function () {
  return (
    <ReactFlowProvider>
      <LayoutFlow />
    </ReactFlowProvider>
  );
}
